{"command": "node -e "
const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');

const MYCEL = '0x94222fbc9ff779f7b02E77B60411BBAC6420f1B3';
const LP = '0x498581fF718922c3f8e6A244956aF099B2652b2b';
const wallet = '0x9FffCdb0bB65Ac66D20179A4874fD965F1b1b2be';

async function check() {
  // ETH balance
  const ethBal = await provider.getBalance(wallet);
  console.log('Wallet ETH:', ethers.formatEther(ethBal));

  // LP ETH
  const lpEth = await provider.getBalance(LP);
  console.log('LP ETH:', ethers.formatEther(lpEth));

  // Simple ERC20 ABI
  const erc20 = new ethers.Contract(MYCEL, [
    'function balanceOf(address) view returns (uint256)',
    'function totalSupply() view returns (uint256)',
    'function symbol() view returns (string)'
  ], provider);

  const sym = await erc20.symbol();
  const supply = await erc20.totalSupply();
  const myBal = await erc20.balanceOf(wallet);
  const lpBal = await erc20.balanceOf(LP);

  console.log('Token:', sym);
  console.log('Total supply:', ethers.formatUnits(supply, 18));
  console.log('Wallet balance:', ethers.formatUnits(myBal, 18));
  console.log('LP balance:', ethers.formatUnits(lpBal, 18));
}

check();
""}

