    .agent-item:last-child{border-bottom:none}
    .agent-name{color:var(--text);min-width:80px}
    .agent-role{color:var(--dim);flex:1}
    .agent-status{width:6px;height:6px;border-radius:50%;background:var(--green)}
    .agent-status.idle{background:var(--amber)}

    .resources{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px}
