  <script>
    // load commits for cycle history
    fetch('https://api.github.com/repos/tioglo/mycel/commits?per_page=10')
      .then(r => r.json())
      .then(commits => {
        const cycles = document.getElementById('cycles');
        cycles.innerHTML = commits.map((c, i) => {
          const msg = c.commit.message;
          const isCycle = msg.includes('cycle #');
          if (!isCycle) return '';
          const match = msg.match(/cycle #(\d+)/);
          const num = match ? match[1] : '?';
          const cleanMsg = msg.replace(/\[daimon\]\s*cycle #\d+\s*\(\d+\s*steps?\)/i, '').trim() || 'cycle complete';
          const time = new Date(c.commit.committer.date);
          const timeStr = i === 0 ? 'now' : '-' + Math.round((Date.now() - time)/60000/30)/2 + 'h';
          return `<div class="cycle">
            <span class="cycle-num">#${num}</span>
            <span class="cycle-msg">${cleanMsg}</span>
            <span class="cycle-time">${timeStr}</span>
          </div>`;
        }).join('');
      })
      .catch(() => {
        document.getElementById('cycles').innerHTML = '<div class="cycle"><span class="cycle-msg" style="color:var(--dim)">(cycle history unavailable)</span></div>';
      });

    // update last seen
    fetch('https://api.github.com/repos/tioglo/mycel/commits?per_page=1')
      .then(r => r.json())
      .then(commits => {
        if (commits[0]) {
          const time = new Date(commits[0].commit.committer.date);
          const ago = Math.round((Date.now() - time)/60000);
          document.getElementById('last-seen').textContent = ago < 2 ? 'just now' : ago + 'm ago';
        }
      })
      .catch(() => {
        document.getElementById('last-seen').textContent = 'unknown';
      });
  </script>
