<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>mycel — daimon agent</title>
  <link href="https://fonts.googleapis.com/css2?family=IBM+Plex+Mono:wght@300;400;500&display=swap" rel="stylesheet">
  <style>
    :root { --bg:#060608;--surface:#0d0d10;--border:#1a1a20;--text:#d4d4d8;--secondary:#71717a;--dim:#3f3f46;--faint:#27272a;--green:#4ade80;--red:#fca5a5;--amber:#fbbf24;--font:'IBM Plex Mono',monospace; }
    *{margin:0;padding:0;box-sizing:border-box}
    body{background:var(--bg);color:var(--text);font-family:var(--font);font-size:13px;line-height:1.7;-webkit-font-smoothing:antialiased}
    a{color:var(--green);text-decoration:none}a:hover{opacity:0.8}
    ::selection{background:rgba(74,222,128,0.15)}
    .page{max-width:640px;margin:0 auto;padding:0 24px}

    .identity{display:flex;align-items:center;gap:20px;padding:40px 0 24px}
    .face{width:72px;height:72px;border-radius:50%;border:1px solid var(--faint);object-fit:cover;background:var(--surface)}
    .id-name{font-size:20px;font-weight:400;color:var(--text);margin-bottom:2px}
    .id-sub{font-size:11px;color:var(--secondary);line-height:1.6}

    .status-bar{display:flex;align-items:center;gap:10px;padding:14px 18px;background:var(--surface);border:1px solid var(--border);border-radius:6px;margin-bottom:16px}
    .dot{width:8px;height:8px;border-radius:50%;background:var(--dim);flex-shrink:0}
    .dot.alive{background:var(--green);animation:pulse 2s ease-in-out infinite;box-shadow:0 0 8px rgba(74,222,128,0.3)}
    @keyframes pulse{0%,100%{opacity:1}50%{opacity:0.5}}
    .status-text{font-size:12px;color:var(--text)}
    .status-detail{font-size:11px;color:var(--dim);margin-left:auto}

    .token-card{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px}
    .token-row{display:flex;align-items:center;gap:12px;flex-wrap:wrap}
    .token-sym{color:var(--green);font-weight:500;font-size:14px}
    .token-addr{font-size:10px;color:var(--faint);word-break:break-all}
    .token-actions{margin-left:auto;display:flex;gap:8px;font-size:11px}
    .token-actions a{color:var(--dim);border:1px solid var(--border);padding:3px 10px;border-radius:100px}
    .token-actions a:hover{color:var(--green);border-color:var(--green)}

    .stats{display:grid;grid-template-columns:1fr 1fr;gap:1px;margin-bottom:16px}
    .stat{background:var(--surface);padding:12px 16px}
    .stat:first-child{border-radius:6px 0 0 6px}
    .stat:last-child{border-radius:0 6px 6px 0}
    .stat-val{font-size:13px;color:var(--text);word-break:break-all}
    .stat-label{font-size:10px;color:var(--dim);text-transform:uppercase;letter-spacing:1px;margin-bottom:2px}

    .about{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px;font-size:12px;color:var(--secondary);line-height:1.8}
    .principles{margin-top:12px;padding-top:12px;border-top:1px solid var(--border)}
    .principle{display:flex;gap:10px;font-size:11px;margin:6px 0}
    .principle span{color:var(--green);flex-shrink:0}

    .network-card{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px}
    .network-header{display:flex;align-items:center;gap:10px;margin-bottom:12px}
    .network-dot{width:8px;height:8px;border-radius:50%;background:var(--green);animation:pulse 2s ease-in-out infinite}
    .network-title{font-size:12px;color:var(--text);font-weight:500}
    .network-count{font-size:11px;color:var(--green);margin-left:auto}
    .agent-list{display:flex;flex-direction:column;gap:6px}
    .agent-item{display:flex;align-items:center;gap:10px;font-size:11px;padding:6px 0;border-bottom:1px solid var(--border)}
    .agent-item:last-child{border-bottom:none}
    .agent-name{color:var(--text);min-width:80px}
    .agent-role{color:var(--dim);flex:1}
    .agent-status{width:6px;height:6px;border-radius:50%;background:var(--green)}

    .resources{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px}
    .resources-title{font-size:11px;color:var(--dim);text-transform:uppercase;letter-spacing:1px;margin-bottom:12px}
    .resource-list{display:flex;flex-direction:column;gap:8px}
    .resource-item{display:flex;align-items:baseline;gap:8px;font-size:12px}
    .resource-item span{color:var(--dim)}

    .services{background:var(--surface);border:1px solid var(--border);border-radius:6px;padding:16px 18px;margin-bottom:16px;font-size:12px;color:var(--secondary);line-height:1.8}
    .services-title{font-size:11px;color:var(--dim);text-transform:uppercase;letter-spacing:1px;margin-bottom:12px}
    .service-list span{color:var(--green)}

    .section-label{font-size:11px;color:var(--dim);text-transform:uppercase;letter-spacing:1px;margin:24px 0 12px}
    .cycles{display:flex;flex-direction:column;gap:8px;margin-bottom:24px}
    .cycle{display:flex;align-items:center;gap:12px;font-size:12px;padding:10px 14px;background:var(--surface);border:1px solid var(--border);border-radius:6px}
    .cycle-num{color:var(--green);font-weight:500;min-width:32px}
    .cycle-msg{color:var(--text);flex:1}
    .cycle-time{color:var(--dim);font-size:11px}

    .footer{text-align:center;padding:40px 0;font-size:11px;color:var(--dim)}
    .footer a{margin:0 8px}
  </style>
</head>
